INSERT INTO public.post_variable (variable_id, post_id, value) VALUES (5, 2, 'Sunt student, ce să aleg: internship sau bootcamp?');
INSERT INTO public.post_variable (variable_id, post_id, value) VALUES (6, 2, 'Publicat de Bogdan Iancu pe 11.01.2018');
